-- Table structure for `roomseat`
-- ----------------------------
DROP TABLE IF EXISTS `roomseat`;
CREATE TABLE `roomseat` (
  `Id` int(11) NOT NULL AUTO_INCREMENT,
  `Row` int(11) NOT NULL COMMENT '行',
  `Col` int(11) NOT NULL COMMENT '列',
  `RoomId` int(11) NOT NULL COMMENT '对应的房间号',
  `State` int(11) NOT NULL DEFAULT '1' COMMENT '1：空着的。0：已被预订',
  `SeatNumber` int(11) NOT NULL COMMENT '座位编号',
  PRIMARY KEY (`Id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

